% ========== Clear All ==========clear ; close all; clc% ========== Read Data ==========rawData = csvread('EPL.csv');% ========== Data Size ==========m = size(rawData, 1);% ========== Get X ==========year = 2016;sum = 0;X = zeros(m/9, 1);for i = 1:m/9  X(i) = (2016 - rawData(9*i - 8, 1)).^2 + 1;  sum = sum + X(i);end% ========== Feature Scaling ==========norm = sum / (m/9);X_norm = (X - norm) ./ (m/9);% ========== Add Feature 0 ==========X_norm = [ones(m/9, 1), X_norm];%X = [ones(m/9, 1) X];X = X_norm;% ========== Get Y ==========y = zeros(m/9, 1);for i =1:m/9  y(i) = rawData(9*i, 2);end% ========== Set Theta ==========theta = ones(2, 1);% ========== Set Learning Rate ==========alpha = 0.04;num_iters = 100;[theta, J_history] = gradientDescentMulti(X, y, theta, alpha, num_iters);plot(1:100, J_history(1:100), '-b', 'LineWidth', 2);plotData(X(:, 2), y);hold on;plot(X(:, 2), X*theta, '-b');rank = [1 (1-norm)./(m/9)] * theta