% ========== Clear All ==========clear ; close all; clc% ========== Read Data ==========rawData = csvread('EPLstat.csv');% ========== Set Value ==========m = size(rawData, 1);n = size(rawData, 2);season = 2016;teamSize = 20;% ========== Get Season Stat ==========seasonData = rawData(3:m, 1);seasonFeature = (season - seasonData).^2 + 1;% ========== Get X ==========X = rawData(3:m, 2:3);X = [seasonFeature X];% ========== Feature Scaling ==========[X_norm, mu, sigma]= featureNormalize(X);X_norm = [ones(length(seasonData), 1) X_norm];% ========== Get y ==========y = rawData(3:m, 4);% ========== Set Theta ==========theta = ones(size(X_norm, 2), 1);% ========== Set Learning Rate ==========alpha = 0.04;% ========== Gradient Descent ==========num_iters = 100;[theta, J_history] = gradientDescentMulti(X_norm, y, theta, alpha, num_iters);% ========== IterXCost Graph ==========plot(1:100, J_history(1:100), '-b', 'LineWidth', 2);% ========== Y & Hypothesis Graph ==========plotData(seasonData, y);hold on;plot(seasonData, X_norm*theta, '-b');% ========== Predict Current Season Point ==========rank = [1 ((1 - mu)./sigma)] * theta